import org.apache.spark.SparkConf
import org.apache.spark.sql.SparkSession
import org.junit.Test

/**
 * @author : Rison 2021/6/15 下午4:09
 *         spark on hive
 *         spark 操作 hive数据库
 */
case class SparkOnHive() {

  @Test
  def testSql(): Unit = {
    val conf = new SparkConf()
      .setAppName(this.getClass.getSimpleName.stripPrefix("$") + "_sql")
      .setMaster("local[*]")

    val spark: SparkSession = SparkSession
      .builder()
      .config(conf)
      .config("spark.driver.host", "lx")
      .enableHiveSupport()
      .getOrCreate()

    import spark.implicits._
    import spark.sql
    val sql =
      """
        |show databases;
        |show tables in szt;
        |use szt;
        |select * from ads_card_deal_day_top;
        |select * from ads_in_out_station_day_top;
        |select * from ads_in_station_day_top;
        |select * from ads_line_send_passengers_day_top;
        |select * from ads_out_station_day_top;
        |""".stripMargin
    val arrSql: Array[String] = sql.split(";")
    arrSql.foreach(
      sql => {
        if (sql.trim.nonEmpty) {
          spark.sql(sql).show(10, 50)
        }
      }
    )
    spark.stop()
  }

  @Test
  def testYarn() = {
    val conf = new SparkConf()
      .setAppName(this.getClass.getSimpleName.stripPrefix("$") + "_yarn")
      .setMaster("yarn")

    val spark: SparkSession = SparkSession
      .builder()
      .config(conf)
      // 多网卡机器，会识别到错误的主机名，要写绝对地址，不要 localhost
      .config("spark.driver.host", "lx")
      .config("spark.jars", "target/sparkOnHive-0.1.jar")
      .enableHiveSupport()
      .getOrCreate()

    import spark.implicits._
    import spark.sql
    spark.sql("show databases").show(100, false)

    spark.stop()

  }

  def testLocal() = {
    val conf = new SparkConf()
      .setAppName(this.getClass.getSimpleName.stripPrefix("$") + "_local")
      .setMaster("yarn")

    val spark: SparkSession = SparkSession
      .builder()
      .config(conf)
      // 多网卡机器，会识别到错误的主机名，要写绝对地址，不要 localhost
      .config("spark.driver.host", "lx")
      .enableHiveSupport()
      .getOrCreate()

    import spark.implicits._
    spark.sql(
      """
        |select * from szt.ods_szt_data limit 100
        |""".stripMargin)
      .show(200, false)
  }
}

